//Function for login  -----> chat menu
//parameter ID, PW
//after read on DB, reponse chat room imformation
exports.login = function(ID, PW, callbck){
	var que = "SELECT * FROM tmp_client WHERE id ='" + ID+"' and PW='"+PW+"'";
	var query = connection.query(que, function(err, rows){
		if(rows.length == 0){
			callback({'result':"N"});
		}	
		else{
			//check chat room
			var que_for_chat = "SELECT no, partner,time_start FROM counseling_log WHERE client='"+ID+"'";
			var query_for_chat = connection.query(que, function(err, rows){
				if(rows.length == 0){
					callback({'result':"Y"});
				}	
				else{
					var no_log = rows[0].no;
					var partner = rows[0].partner;
					var time_start = rows[0].time_start;
					
					callback({'result':"C",
						'no_log':no_log,
						'partner':partner,
						'time_start':time_start,
						'method':"login"
					});
				}
			});
		}
	});
}

